T = TypeVar('T')

class Stack(Generic[T]):
    def __init__(self) -> None:
        self.stack: List[T] = []

    def push(self, item: T) -> None:
        self.stack.append(item)

stack = Stack[int]()
stack.push(1) # [1]
stack.push(2) # [1, 2]
stack.push("3") # TypeError

