def validate_age_and_name(cls):
    initCls = cls.__init__

    def new_init(self, name, age):
        if age < 0:
            raise ValueError("Age cannot be negative")
        elif not isinstance(name, str) or len(name) == 0:
            raise ValueError("Name must be a non-empty string")
        initCls(self, name, age)

    cls.__init__ = new_init
    return cls

@validate_age_and_name
class Person:
    def __init__(self, name, age):
        self.name = name
        self.age = age

try:
    p1 = Person("Alice", 30)  # Valid
    p2 = Person("", 25)       # ValueError: Name must be a non-empty string
    p3 = Person("Bob", -5)    # ValueError: Age cannot be negative
except ValueError as e:
    print(e)

Magic Methods.content:
# Arithmetic magic methods
class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def __add__(self, other):  # p1 + p2
        return Point(self.x + other.x, self.y + other.y)

    def __sub__(self, other):  # p1 - p2
        return Point(self.x - other.x, self.y - other.y)

    def __mul__(self, other):  # p1 * p2
        return Point(self.x * other.x, self.y * other.y)

    def __truediv__(self, other):  # p1 / p2
        return Point(self.x / other.x, self.y / other.y)

# Container magic methods
class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def __getitem__(self, index):  # p[index]
        if index == 0:
            return self.x
        elif index == 1:
            return self.y
        else:
            raise IndexError("Index out of range")

    def __setitem__(self, index, value):  # p[index] = value
        if index == 0:
            self.x = value
        elif index == 1:
            self.y = value
        else:
            raise IndexError("Index out of range")

# Other magic methods
class Person:
    def __init__(self, name, age):
        self.name = name
        self.age = age

    def __str__(self):  # str(p)
        return f'Name: {self.name}, Age: {self.age}'

    def __repr__(self):  # repr(p)
        return f'Person({self.name}, {self.age})'

    def __len__(self):  # len(p)
        return len(self.name)

